import React, {useState} from 'react';
import * as sessionActions from "../../store/session";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from 'react-router-dom';
import { Link } from 'react-router-dom';
import QuestRabbit from '../../images/QuestRabbit.jpg';

function Login() {
  const dispatch = useDispatch();
  const [username, setUserName] = useState('');
  const [password, setpassword] = useState('');
  const [errors, setErrors] = useState('');
  const currentUser = useSelector(state => state.session.user);

  if (currentUser) return <Redirect to='/' />;

  const renderErrors=()=> {
    if (!errors) return '';
    return(
      <ul>
        {errors.map((error, i) => (
          <li 
          key={`error-${i}`}
          className="error"
          >
            {error}
          </li>
        ))}
      </ul>
    );
  }



  const handleSubmit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setErrors([]);
    return dispatch(sessionActions.login({ username, password }))
      .catch(async (res) => {
        let data;
        try {
          // .clone() essentially allows you to read the response body twice
          data = await res.clone().json();
        } catch {
          data = await res.text(); // Will hit this case if the server is down
        }
        if (data?.errors) setErrors(data.errors);
        else if (data) setErrors([data]);
        else setErrors([res.statusText]);
      });
  }

    const loginGuest=()=> {
    const guest = {
      username: 'Guest',
      password: 'hunter12'
      };
        return dispatch(sessionActions.login(guest));
    }

    const logo = window.logo;
    const formPhoto = window.formPhoto;
    return (
      <div className="session-form">
       <img src={formPhoto} className="form-photo"/>
        <form className='inter-form'>
          <Link to='/'>
          <img src={logo} className="logo"/>
          </Link>
          {renderErrors()}
          <input
            type="text"
            value={username}
            onChange={e => setUserName(e.target.value)}
            placeholder="Username"
          />
          <input
            type="password"
            value={password}
            onChange={e=>setpassword(e.target.value)}
            placeholder="Password"
          />
           <button onClick={handleSubmit}>Log in</button>
            <button
            to="/"
            onClick={loginGuest}
            className="login-guest" >
            Demo as Guest
          </button>

      </form>
      </div>
    );
  };


export default Login;
